import Typewriter from "typewriter-effect";
import "./App.css";

import { isMobile } from "react-device-detect";
import { useState } from "react";
import styled, { ThemeProvider } from "styled-components";
import { lightTheme, darkTheme, GlobalStyles } from "./themes.js";

const StyledApp = styled.div``;

function App() {
  const [theme, setTheme] = useState("light");

  const themeToggler = () => {
    theme === "light" ? setTheme("dark") : setTheme("light");
  };

  return (
    <ThemeProvider
      theme={theme === "light" ? lightTheme : darkTheme}
      style={{}}
    >
      <GlobalStyles />
      <StyledApp className={isMobile ? "App App-mobile" : "App"}>
        <Typewriter
          // options={{
          //   strings: ["coming", "soon"],
          //   autoStart: true,
          //   loop: true,
          //   deleteSpeed: 200,
          //   delay: 400,
          // }}
          onInit={(typewriter) => {
            typewriter
              .changeDelay(200)
              .typeString("comu")
              .pauseFor(400)
              .deleteChars(1)
              .changeDelay(200)
              .typeString("ingh soo")
              .pauseFor(200)
              .deleteChars(5)
              .changeDelay(100)
              .typeString(" aion")
              .pauseFor(300)
              .deleteAll()
              .changeDelay(30)
              .typeString("ASDHKAJSHDZ")
              .deleteAll()
              .pauseFor(1000)
              .changeDelay(90)
              .typeString("coming")
              .changeDelay(300)
              .typeString(" soon")
              .start();
          }}
        />
      </StyledApp>
      <button onClick={themeToggler}>CLICK TO CHANGE THEME</button>
    </ThemeProvider>
  );
}

export default App;
