import React, { useState, useEffect } from "react";
import "./Content.css";
import Typewriter from "typewriter-effect";
import { isMobile } from "react-device-detect";
import Card from "./Card";

function Content(props) {
  const [offsetY, setOffsetY] = useState(0);
  const handleScroll = () => setOffsetY(window.pageYOffset);
  const lorem =
    "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Debitis adipisci nisi fugit dolorem, pariatur natus, veritatis sequi soluta animi quia eum corrupti, necessitatibus totam alias reiciendis tenetur deleniti ullam aliquid!";

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  function checkOffset() {
    console.log(offsetY);
  }

  return (
    <div>
      <div
        id="home"
        className={isMobile ? "home-style home-style-mobile" : "home-style"}
      >
        <h1>
          <Typewriter
            onInit={(typewriter) => {
              typewriter
                .changeDelay(100)
                .typeString("claudio")
                .changeDelay(200)
                .typeString(" koesnadi")
                .start();
            }}
          />
        </h1>
      </div>
      <div
        id="about"
        className={isMobile ? "about-style about-style-mobile" : "about-style"}
      >
        <div
          className={isMobile ? "about-text about-text-mobile" : "about-text"}
        >
          <h1
            className={
              isMobile ? "about-header about-header-mobile" : "about-header"
            }
          >
            about me.
          </h1>
          hey! my name's claudio and i'm a software engineer. i have a
          bachelor's in computer science and i am extremely passionate about
          machine learning and artificial intelligence.
        </div>
        <div
          className={
            isMobile ? "picture-dark picture-dark-mobile" : "picture-dark"
          }
          style={{ transform: `translateY(${offsetY * 0.1}px)` }}
        ></div>
      </div>

      <div id="project" className="project-style">
        <div>
          <h1 className="project-header" onClick={checkOffset}>
            projects.
          </h1>
          <div
            className={
              isMobile ? "project-cards project-cards-mobile" : "project-cards"
            }
          >
            <Card
              title="CARD TITLE"
              body={lorem}
              anim={
                offsetY >= 1676
                  ? { transform: `0px` }
                  : { transform: `translateX(${-650 + offsetY * 0.4}px)` }
              }
            />
            <Card
              title="CARD TITLE"
              body={lorem}
              anim={
                offsetY >= 1676
                  ? { transform: `0px` }
                  : { transform: `translateX(${-980 + offsetY * 0.6}px)` }
              }
            />
            <Card
              title="CARD TITLE"
              body={lorem}
              anim={
                offsetY >= 1676
                  ? { transform: `0px` }
                  : { transform: `translateX(${-1300 + offsetY * 0.8}px)` }
              }
            />
            <Card
              title="CARD TITLE"
              body={lorem}
              anim={
                offsetY >= 1676
                  ? { transform: `0px` }
                  : { transform: `translateX(${-640 + offsetY * 0.4}px)` }
              }
            />
          </div>
        </div>
      </div>

      <div id="contact" className="contact-style">
        <div>
          <h1 className="contact-header">contact section</h1>
          <div className="contact-text">
            Lorem ipsum dolor sit, amet consectetur adipisicing elit. Velit
            voluptate sapiente possimus? Magni porro, consectetur excepturi
            quibusdam amet recusandae reiciendis error assumenda labore sed
            voluptates in deleniti, atque doloribus explicabo! Lorem ipsum dolor
            sit, amet consectetur adipisicing elit. Accusantium aliquid vitae
            reiciendis in suscipit possimus dolore recusandae eius iusto,
            pariatur odit veniam, maxime ducimus sequi adipisci quam ipsa?
            Delectus, quos.
          </div>
        </div>
      </div>
    </div>
  );
}

export default Content;
